package demonstracao;

public class Variaveis {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Tipos primitivos
		char j = 'j';
		int n1 = 20;
		long handle = 1234567l;
		
		// Wrapper classes
		Long n2 = 1234567l;
		Double n3 = 2.15;
		String str = "Jeferson";
		
		System.out.println("char = " + j +
						   "\nint = " + n1 + 
						   "\nlong = " +  handle + 
						   "\nLong = " +  n2 + 
						   "\nDouble = " + n3 + 
						   "\nString = " + str);
	}
}